document.addEventListener("DOMContentLoaded",()=>{const e=document.documentElement,t=document.getElementById("theme-toggle"),n=localStorage.getItem("theme")||(window.matchMedia("(prefers-color-scheme: dark)").matches?"dark":"light");n==="dark"&&e.classList.add("dark"),t.addEventListener("click",()=>{e.classList.toggle("dark"),localStorage.setItem("theme",e.classList.contains("dark")?"dark":"light")})})